import React, { useState, useEffect } from "react";
import { Bar } from "react-chartjs-2";
import sourceData from "./data/sourceData.json";

export const Chart = () => {
  const [chartRef, setChartRef] = useState(null);

  useEffect(() => {
    // Hủy biểu đồ cũ trước khi tạo biểu đồ mới
    if (chartRef) {
      chartRef.destroy();
    }

    // Tạo biểu đồ mới
    const newChart = new Chart(chartRef, {
      type: "bar",
      data: {
        labels: sourceData.map((data) => data.label),
        datasets: [
          {
            label: "Count",
            data: sourceData.map((data) => data.value),
            backgroundColor: [
              "rgba(255, 99, 132, 1)",
              "rgba(54, 162, 235, 1)",
              "rgba(255, 206, 86, 1)",
              "rgba(75, 192, 192, 1)",
            ],
            borderRadius: 10,
          },
        ],
      },
    });

    // Lưu tham chiếu đến biểu đồ
    setChartRef(newChart);

    // Cleanup khi component unmount
    return () => {
      if (newChart) {
        newChart.destroy();
      }
    };
  }, [chartRef]);

  return <div className="smallChart customerCard"><canvas ref={setChartRef} /></div>;
};
